// Privacy.js
import React from 'react';

const Privacy = () => {
    return (
        <div className="container my-5">
            <h1 className="mb-4">Privacy Policy</h1>
            <p>At [Your Company], we take your privacy seriously. This privacy policy outlines how we collect, use, and protect your personal information.</p>

            <h3 className="mt-4">1. Information Collection</h3>
            <p>We may collect personal details such as name, email address, phone number, and payment information when you use our services.</p>

            <h3 className="mt-4">2. Use of Information</h3>
            <p>The information we collect is used to improve your user experience, process transactions, and communicate with you regarding updates or offers.</p>

            <h3 className="mt-4">3. Data Protection</h3>
            <p>We implement security measures to protect your personal data from unauthorized access, alteration, or destruction.</p>

            <h3 className="mt-4">4. Third-Party Sharing</h3>
            <p>We do not share your personal data with third parties unless necessary for processing your requests or as required by law.</p>

            <h3 className="mt-4">5. Your Rights</h3>
            <p>You have the right to access, correct, or delete your personal information at any time. Please contact us if you wish to exercise these rights.</p>

            <h3 className="mt-4">6. Changes to This Policy</h3>
            <p>We may update this privacy policy periodically. All updates will be posted on this page with the updated date at the bottom.</p>

            <p className="mt-4">Last updated: [Date]</p>
        </div>
    );
};

export default Privacy;
