import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';

const CartPage = () => {
    const { cartItem, removeFromCart, updateQuantity } = useContext(CartContext);
    const navigate = useNavigate();

    const handleRemoveFromCart = (id) => {
        removeFromCart(id);
    };

    const handleQuantityChange = (id, quantity) => {
        const parsedQuantity = parseInt(quantity, 10) || 1;  // Default to 1 if input is invalid or empty
        updateQuantity(id, parsedQuantity);
    };

    const handleContinueShopping = () => {
        navigate("/product");
    };

    const handleCheckOut = () => {
        navigate("/checkout");
    };

    return (
        <div className="bg-light py-5 min-vh-100">
            <div className="container">
                <h1 className="text-center mb-4">Shopping Cart</h1>
                <div className="row">
                    {/* Cart Items Section */}
                    <div className="col-12 col-lg-8 mb-4 mb-lg-0">
                        <div className="card shadow-sm">
                            <div className="table-responsive">
                                <table className="table table-bordered">
                                    <thead className="thead-light">
                                        <tr>
                                            <th>Product</th>
                                            <th>Quantity</th>
                                            <th>Price</th>
                                            <th>Remove</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {cartItem.length === 0 ? (
                                            <tr>
                                                <td colSpan="4" className="text-center py-3">
                                                    Your cart is empty.
                                                </td>
                                            </tr>
                                        ) : (
                                            cartItem.map((item) => (
                                                <tr key={item.id}>
                                                    <td>
                                                        <div className="d-flex align-items-center">
                                                            <img
                                                                src={item.image}
                                                                alt={item.name}
                                                                className="img-fluid"
                                                                style={{ width: '50px', height: '50px' }}
                                                            />
                                                            <span className="ms-3">{item.name}</span>
                                                        </div>
                                                    </td>
                                                    <td>
                                                        <input
                                                            type="number"
                                                            className="form-control"
                                                            min="1"
                                                            value={item.quantity || 1}  // Ensure the value is never empty or invalid
                                                            onChange={(e) =>
                                                                handleQuantityChange(item.id, e.target.value)
                                                            }
                                                        />
                                                    </td>
                                                    <td>${item.price * item.quantity}</td>
                                                    <td>
                                                        <button
                                                            onClick={() => handleRemoveFromCart(item.id)}
                                                            className="btn btn-danger btn-sm"
                                                        >
                                                            Remove
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))
                                        )}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>

                    {/* Order Summary Section */}
                    <div className="col-12 col-lg-4">
                        <div className="card shadow-sm">
                            <div className="card-body">
                                <h5 className="card-title">Order Summary</h5>
                                <div className="d-flex justify-content-between mb-3">
                                    <span>Subtotal</span>
                                    <span>
                                        ${cartItem.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2)}
                                    </span>
                                </div>
                                <div className="d-flex justify-content-between mb-3">
                                    <span>Tax</span>
                                    <span>$5.00</span>
                                </div>
                                <div className="d-flex justify-content-between mb-3 border-top pt-3">
                                    <strong>Total</strong>
                                    <strong>
                                        ${(
                                            cartItem.reduce((total, item) => total + item.price * item.quantity, 0) + 5.0
                                        ).toFixed(2)}
                                    </strong>
                                </div>
                                <button
                                    onClick={handleCheckOut}
                                    className="btn btn-primary w-100 py-2 mt-3"
                                >
                                    Proceed to Checkout
                                </button>
                                <button
                                    onClick={handleContinueShopping}
                                    className="btn btn-outline-secondary w-100 py-2 mt-2"
                                >
                                    Continue Shopping
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CartPage;
