import React, { useState, useEffect, useContext } from 'react';
import { CartContext } from "../context/CartContext"; // Make sure to import context
import { Link } from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Product.css';

const Products = () => {
    const { addToCart, cart } = useContext(CartContext); // Access context
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((response) => response.json())
            .then((data) => {
                const filteredProducts = data.filter(product => product.category !== "electronics" && product.category !== "jewelery");
                setProducts(filteredProducts);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching products:', error);
                setLoading(false);
            });
    }, []);

    if (loading) return <p className="container">Loading...</p>;

    return (
        <div className="container mt-4">
            <div className="d-flex flex-wrap justify-content-between align-items-center mb-4">
                <h1 className="text-start mt-5 hover-underline">COLLECTION</h1>
            </div>

            <div className="row">
                {products.length > 0 ? (products.map((product) => (
                    <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={product.id}>
                        <div className="card h-100">
                            <Link to={`/product/${product.id}`}>
                                <img
                                    src={product.image}
                                    className="card-img-top"
                                    alt={product.title}
                                    style={{ height: '250px', objectFit: 'contain' }}
                                />
                            </Link>
                            <div className="card-body">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">${product.price}</p>
                                <Link to={`/product/${product.id}`} className="btn btn-primary mb-2 me-2">
                                    View Details
                                </Link>

                                {/* Cart Icon */}
                                <button
                                    className="btn btn-outline-success"
                                    onClick={() => addToCart(product)} // Add product to cart
                                    disabled={cart.some((item) => item.id === product.id)}
                                >
                                    <i
                                        className={`fas fa-shopping-cart ${cart.some((item) => item.id === product.id) ? 'text-success' : ''}`}
                                    ></i>
                                </button>
                            </div>
                        </div>
                    </div>
                ))
                ) : (
                    <p className="text-center mt-4">No products available at the moment. Please check back later!</p>
                )}
            </div>
        </div>
    );
};

export default Products;
