import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

const CheckoutForm = () => {
    const { cartItem, emptyCart } = useContext(CartContext);
    const [isShippingDifferent, setIsShippingDifferent] = useState(false);
    const [createAccount, setCreateAccount] = useState(false);

    // Calculate total amount from cart items
    const totalAmount = cartItem.reduce(
        (sum, item) => sum + item.price * item.quantity,
        0
    );

    // Handle toggling the shipping address
    const handleShippingToggle = () => {
        setIsShippingDifferent(!isShippingDifferent);
    };

    // Handle account creation toggle
    const handleCreateAccountToggle = () => {
        setCreateAccount(!createAccount);
    };

    const handlePaymentSuccess = (details, data) => {
        alert(`Transaction completed by ${details.payer.name.given_name}`);
        console.log("Transaction Details:", details, data);
        emptyCart();
    };

    // Initial PayPal options
    const initialPayPalOptions = {
        "client-id": "AZsg0CU6mNHveGmpF90bMucCtWGs2ZKPUKg7B2iQzzFYvQoYcH2p_L_MREqXMJgYPtjeZ43Fq11uDhIf",
        currency: "USD",
    };

    return (
        <div>
            {/* Page Header */}
            <div className="bg-gray-100 py-12">
                <div className="container mx-auto text-center">
                    <p className="text-xl text-gray-500">Fresh and Organic</p>
                    <h1 className="text-4xl font-extrabold text-gray-900">Check Out Product</h1>
                </div>
            </div>

            {/* Checkout Section */}
            <div className="checkout-section py-12">
                <div className="container mx-auto px-6">
                    <form>
                        <div className="flex flex-wrap gap-6">
                            {/* Shipping Form */}
                            <div className="w-full lg:w-2/3">
                                <h3 className="text-xl font-bold text-gray-800 mb-6">Shipping Address</h3>
                                <label className="text-sm font-semibold text-gray-700" htmlFor="name">
                                    Full Name
                                </label>
                                <input
                                    type="text"
                                    name="name"
                                    id="name"
                                    required
                                    className="block w-full p-3 border border-gray-300 rounded-md mb-4"
                                />
                                <label className="text-sm font-semibold text-gray-700" htmlFor="address">
                                    Address
                                </label>
                                <input
                                    type="text"
                                    name="address"
                                    id="address"
                                    required
                                    className="block w-full p-3 border border-gray-300 rounded-md mb-4"
                                />
                                {/* Toggle Shipping Address */}
                                <button
                                    type="button"
                                    onClick={handleShippingToggle}
                                    className="text-blue-600"
                                >
                                    Use a different shipping address
                                </button>
                            </div>

                            {/* Billing Form */}
                            <div className="w-full lg:w-1/3">
                                <h3 className="text-xl font-bold text-gray-800 mb-6">Billing Information</h3>
                                <label className="text-sm font-semibold text-gray-700" htmlFor="billing-name">
                                    Name on Card
                                </label>
                                <input
                                    type="text"
                                    name="billing-name"
                                    id="billing-name"
                                    required
                                    className="block w-full p-3 border border-gray-300 rounded-md mb-4"
                                />
                                <label className="text-sm font-semibold text-gray-700" htmlFor="card">
                                    Credit Card Number
                                </label>
                                <input
                                    type="text"
                                    name="card"
                                    id="card"
                                    required
                                    className="block w-full p-3 border border-gray-300 rounded-md mb-4"
                                />
                                {/* Toggle Account Creation */}
                                <div className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id="create-account"
                                        checked={createAccount}
                                        onChange={handleCreateAccountToggle}
                                    />
                                    <label htmlFor="create-account" className="ml-2 text-sm text-gray-700">
                                        Create an account?
                                    </label>
                                </div>
                            </div>
                        </div>

                        <div className="mt-6">
                            {/* PayPal Buttons */}
                            <PayPalScriptProvider options={initialPayPalOptions}>
                                <PayPalButtons
                                    createOrder={(data, actions) => {
                                        return actions.order.create({
                                            purchase_units: [
                                                {
                                                    amount: {
                                                        value: totalAmount.toFixed(2),
                                                    },
                                                },
                                            ],
                                        });
                                    }}
                                    onApprove={handlePaymentSuccess}
                                />
                            </PayPalScriptProvider>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default CheckoutForm;
