import React from 'react';
import { Link } from "react-router-dom"
import "./Cart.css";
import Payment from './Payment';


const Cart = ({ cart, removeFromCart }) => {
    const totalAmount = cart.reduce((total, item) => total + item.price, 0);

    return (
        <div className="container mt-4">
            <Link to={`/product/${product.id}`}>
                <h2>Shopping Cart</h2>
                {cart.length === 0 ? (
                    <div>
                        <p >Your cart is empty.
                            <Link to="/product" className='m-1'>Shopping Now</Link>
                        </p>

                    </div>

                ) : (
                    <div className="row mt-5 mb-5">
                        {cart.map((item) => (
                            <div className="col-md-3 mb-4" key={item.id}>
                                <div className="card h-100">
                                    <img src={item.image} className="card-img-top mx-auto" alt={item.title} style={{ height: '200px', objectFit: 'contain' }} />
                                    <div className="card-body">
                                        <h5 className="card-title">{item.title}</h5>
                                        <p className="card-text">${item.price.toFixed(2)}</p>
                                        <button
                                            className="btn btn-danger"
                                            onClick={() => removeFromCart(item.id)}
                                        >
                                            Remove from Cart
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}

                {/* Display Total and Pass it to Payment Component */}
                {cart.length > 0 && (
                    <div className="mt-4 mb-4">
                        <h3 className="text-center border-top border-black pt-3 mt-4">Proceed to Payment</h3>
                        <Payment totalAmount={totalAmount} />
                    </div>
                )}
            </Link>
        </div>
    );
};

export default Cart;
